# ---------- safe wrappers -------------------------------------------------
def safe_openai_chat(**kwargs) -> str | None:
    """Call the OpenAI ChatCompletion endpoint with basic retry logic.
       Returns the *raw string* response, or None if it ultimately fails.
    """
    for attempt in range(3):                                    # 3 tries
        try:
            resp = openai.ChatCompletion.create(**kwargs)
            return resp.choices[0].message["content"]
        except (APIError, RateLimitError, OpenAIError) as e:
            print(f"  OpenAI error ({e}); retry {attempt+1}/3 …")
            time.sleep(2 + 2*attempt)                          # back-off
        except Exception as e:
            print(f"  Unknown OpenAI exception: {e}")
            break
    return None                                                 # give up


def safe_claude_chat(payload: dict, headers: dict,
                     timeout: int = 30) -> str | None:
    """Call the Anthropic endpoint safely; return text or None."""
    for attempt in range(3):
        try:
            r = requests.post(CLAUDE_API_URL,
                              headers=headers, json=payload,
                              timeout=timeout)
            r.raise_for_status()
            data = r.json()
            txt_blocks = [blk["text"] for blk in data.get("content", [])
                          if blk.get("type") == "text"]
            return "".join(txt_blocks)
        except (requests.RequestException, json.JSONDecodeError) as e:
            print(f"  Claude error ({e}); retry {attempt+1}/3 …")
            time.sleep(2 + 2*attempt)
    return None
# --------------------------------------------------------------------------
def safe_deepseek_chat(payload: dict, timeout: int = DEEPSEEK_TIMEOUT) -> str | None:
    """POST to DeepSeek with simple back-off; return raw assistant text."""
    for attempt in range(3):
        try:
            r = requests.post(DEEPSEEK_API_URL,
                              headers={"Authorization": f"Bearer {DEEPSEEK_API_KEY}",
                                       "Content-Type": "application/json"},
                              json=payload,
                              timeout=timeout)
            r.raise_for_status()
            data = r.json()                 # {"choices":[{"message":{"content":"A"}}] …}
            return data["choices"][0]["message"]["content"]
        except (requests.RequestException, json.JSONDecodeError) as e:
            print(f"  DeepSeek error ({e}); retry {attempt+1}/3 …")
            time.sleep(2 + 2*attempt)
    return None

# ---------- safe wrapper for Mistral ----------------------------
def safe_mistral_chat(messages: list[dict], model: str, max_tokens: int) -> str | None:
    """Call the Mistral client.chat.complete endpoint with retry logic."""
    for attempt in range(3):
        try:
            resp = mistral_client.chat.complete(
                model=model,
                messages=messages,
                max_tokens=max_tokens,
            )
            # Mistral client returns an object with .choices[0].message.content
            return resp.choices[0].message.content
        except Exception as e:
            print(f"  Mistral error ({e}); retry {attempt+1}/3 …")
            time.sleep(2 + 2*attempt)
    return None

def safe_llmapi_chat(payload: dict, max_retries: int = 3) -> str | None:
    """POST to Llama-3.2 endpoint with retries; return assistant text or None."""
    headers = {
        "Authorization": f"Bearer {LLMAPI_KEY}",
        "Content-Type": "application/json",
    }
    for attempt in range(max_retries):
        try:
            r = requests.post(LLMAPI_URL, headers=headers, json=payload, timeout=30)
            r.raise_for_status()
            data = r.json()
            # response schema: {"choices":[{"message":{"content":"…"}}], …}
            return data["choices"][0]["message"]["content"]
        except Exception as e:
            print(f"  Llama-3.2 error ({e}); retry {attempt+1}/{max_retries} …")
            time.sleep(1 + attempt)
    return None

def safe_gemini_chat(contents: str,
                     model: str = GEMINI_MODEL,
                     max_retries: int = 3) -> str | None:
    """
    Call Gemini generate_content with simple retry/backoff.
    Returns the raw assistant text or None on repeated failure.
    """
    for attempt in range(max_retries):
        try:
            response = gemini_client.models.generate_content(
                model=model,
                contents=contents
            )
            return response.text
        except Exception as e:
            print(f"  Gemini error ({e}); retry {attempt+1}/{max_retries} …")
            time.sleep(2 + 2*attempt)
    return None


