def get_openai_answer(q, model="gpt-3.5-turbo", max_tokens=5):
    prompt = f"""You are a medical expert. Please answer the following multiple-choice
question by replying with ONLY the letter (A, B, C, or D).

Question: {q['question']}
A) {q.get('A','')}
B) {q.get('B','')}
C) {q.get('C','')}
D) {q.get('D','')}
"""
    content = safe_openai_chat(
        model=model,
        messages=[{"role": "system", "content": "You are a helpful assistant."},
                  {"role": "user",    "content": prompt}],
        max_tokens=max_tokens,
        temperature=0
    )
    return content.strip().upper() if content else "N/A"


def get_claude_answer(q, model=CLAUDE_MODEL, max_tokens=5):
    prompt = f"""You are a medical expert. Please answer the following \
multiple-choice question by responding with ONLY the letter (A, B, C, or D).

Question: {q['question']}
A) {q.get('A','')}
B) {q.get('B','')}
C) {q.get('C','')}
D) {q.get('D','')}
"""
    hdrs = {
        "x-api-key": CLAUDE_API_KEY,
        "anthropic-version": CLAUDE_API_VERSION,
        "content-type": "application/json"
    }
    payload = {
        "model": model,
        "max_tokens": max_tokens,
        "messages": [{"role": "user", "content": prompt}]
    }
    content = safe_claude_chat(payload, hdrs)
    return content.strip().upper() if content else "N/A"

def get_deepseek_answer(q: dict,
                        model: str = DEEPSEEK_MODEL,
                        max_tokens: int = 5) -> str:
    prompt = f"""You are a medical expert. Please answer the following multiple-choice
question by replying with ONLY the single letter (A, B, C, or D).

Question: {q['question']}
A) {q.get('A','')}
B) {q.get('B','')}
C) {q.get('C','')}
D) {q.get('D','')}
"""
    payload = {
        "model": model,
        "max_tokens": max_tokens,
        "messages": [
            {"role": "user", "content": prompt}
        ]
    }
    txt = safe_deepseek_chat(payload)
    return txt.strip().upper() if txt else "N/A"

def get_mistral_answer(q: dict, model: str = MISTRAL_MODEL, max_tokens: int = 5) -> str:
    prompt = f"""You are a medical expert. Please answer the following multiple-choice
question by replying with ONLY the letter (A, B, C, or D).

Question: {q['question']}
A) {q.get('A','')}
B) {q.get('B','')}
C) {q.get('C','')}
D) {q.get('D','')}
"""
    messages = [
        {"role": "user", "content": prompt}
    ]
    content = safe_mistral_chat(messages, model=model, max_tokens=max_tokens)
    return content.strip().upper() if content else "N/A"

def get_llmapi_answer(q: dict, model: str = LLMAPI_MODEL, max_tokens: int = 5) -> str:
    """Build the MCQ prompt and return the single-letter answer from Llama-3.2."""
    prompt = (
        f"You are a medical expert. Answer with ONLY A, B, C or D.\n\n"
        f"Question: {q['question']}\n"
        f"A) {q.get('A','')}\n"
        f"B) {q.get('B','')}\n"
        f"C) {q.get('C','')}\n"
        f"D) {q.get('D','')}\n"
    )
    payload = {
        "model": model,
        "max_tokens": max_tokens,
        "messages": [{"role": "system", "content": "You are a helpful assistant."},
                     {"role": "user",   "content": prompt}],
    }
    txt = safe_llmapi_chat(payload)
    return txt.strip().upper() if txt else "N/A"

def get_gemini_answer(q: dict,
                      model: str = GEMINI_MODEL) -> str:
    """
    Build a multiple-choice MCQ prompt and return a single letter (A/B/C/D),
    or 'N/A' if it failed.
    """
    prompt = (
        "You are a medical expert. Answer the following question with ONLY the single "
        "letter (A, B, C, or D) that is correct.\n\n"
        f"Question: {q['question']}\n"
        f"A) {q.get('A','')}\n"
        f"B) {q.get('B','')}\n"
        f"C) {q.get('C','')}\n"
        f"D) {q.get('D','')}\n"
    )
    txt = safe_gemini_chat(prompt, model=model)
    return txt.strip().upper() if txt else "N/A"

def check_answer_llm(model_ans, correct_ans,
                     model="gpt-3.5-turbo", max_tokens=3):
    if model_ans in ("N/A", ""):               # we skipped / failed
        return "skipped"
    verdict = safe_openai_chat(
        model=model,
        messages=[{"role": "system",
                   "content": "You are a helpful assistant checking MCQ answers."},
                  {"role": "user",
                   "content": (f"The model answered: {model_ans}\n"
                               f"The correct answer is: {correct_ans}\n"
                               "Return ONLY the word correct or incorrect.")}],
        max_tokens=max_tokens,
        temperature=0
    )
    return verdict.strip().lower() if verdict else "skipped"


